const listeBP=document.querySelector("#listeBP");fetch("../../data/data.json").then(response=>{return response.json()}).then(data=>{data.forEach(d=>{const div=document.createElement("div");div.classList.add("card");div.classList.add(d.category);const p=document.createElement("p");p.textContent=d.name;const divCorner=document.createElement("div");divCorner.classList.add("go-corner");const divArrow=document.createElement("div");divArrow.classList.add("go-arrow");divArrow.textContent="X";divCorner.appendChild(divArrow);div.appendChild(p);div.appendChild(divCorner);if(d.incontournable){divCorner.classList.add("color");divArrow.textContent="✔";div.classList.add("selected")}else{div.addEventListener("click",()=>{if(divCorner.classList.contains("color")){divCorner.classList.remove("color");divArrow.textContent="X";div.classList.remove("selected")}else{divCorner.classList.add("color");divArrow.textContent="✔";div.classList.add("selected")}})}listeBP.appendChild(div)});filterSelection("all")});function filterSelection(c){var x,i;x=document.getElementsByClassName("card");if(c=="all"){c=""}for(i=0;i<x.length;i+=1){w3RemoveClass(x[i],"show");if(x[i].className.indexOf(c)>-1){w3AddClass(x[i],"show")}}}function w3AddClass(element,name){var i,arr1,arr2;arr1=element.className.split(" ");arr2=name.split(" ");for(i=0;i<arr2.length;i+=1){if(arr1.indexOf(arr2[i])==-1){element.className+=" "+arr2[i]}}}function w3RemoveClass(element,name){var i,arr1,arr2;arr1=element.className.split(" ");arr2=name.split(" ");for(i=0;i<arr2.length;i+=1){while(arr1.indexOf(arr2[i])>-1){arr1.splice(arr1.indexOf(arr2[i]),1)}}element.className=arr1.join(" ")}const btnContainer=document.getElementById("myBtnContainer");const btns=btnContainer.querySelectorAll(".btn");btns.forEach((b)=>{b.addEventListener("click",function(){var current=document.getElementsByClassName("active");current[0].className=current[0].className.replace(" active","");this.className+=" active";filterSelection(b.id)})});