const listeBP=document.querySelector("#listeBP");fetch("../../data/data.json").then(response=>{return response.json()}).then(data=>{data.forEach(d=>{const div=document.createElement("div");div.classList.add("card");div.classList.add(d.category);div.textContent=d.name;if(d.incontournable){div.classList.add("selected")}else{div.addEventListener("click",()=>{if(div.classList.contains("selected")){div.classList.remove("selected");if(document.querySelector(".active").id=="selected"){filterSelection("selected")}}else{div.classList.add("selected")}})}listeBP.appendChild(div)});filterSelection("all")});function filterSelection(filter){const cards=document.querySelectorAll(".card");if(filter=="all"){filter="card"}cards.forEach((c)=>{c.classList.remove("show");if(c.classList.contains(filter)){c.classList.add("show")}})}const btns=document.querySelectorAll("#myBtnContainer .btn");btns.forEach((b)=>{b.addEventListener("click",function(){const current=document.querySelector(".active");current.classList.remove("active");this.classList.add("active");filterSelection(b.id)})});